export default class Preview extends Component {
	constructor(props) {
		super(props);
		const showUnavailableNew = this.checkShowUnavailableNew();
		this.timeoutFadeIn = null;
		this.opacity = new Value(0);
		this.prePage = 1;
		this.isShowAffectedSymbol = true;
		this.translateY = this.props.translateY;
		this.documentType = this.checkDocumentTypeNews();
		this.path = '';
		this.state = {
			page: 1,
			visible: false,
			pageCount: 1,
			url: '',
			html: '',
			isPdf: false,
			isLoading: !showUnavailableNew,
			loaded: false
		};
	}

	checkShowUnavailableNew = this.checkShowUnavailableNew.bind(this);
	checkShowUnavailableNew() {
		const { showUnavailableNew } = this.props.data;
		if (showUnavailableNew) {
			return true;
		}
		return false;
	}

	checkDocumentTypeNews = this.checkDocumentTypeNews.bind(this);
	checkDocumentTypeNews() {
		// Hiện fake nên check qua link, về sau check qua type của bản ghi news detail
		const { data } = this.props;
		const { document_type: documentType } = data;
		return documentType;
	}

	clearFadeInTimeOut = this.clearFadeInTimeOut.bind(this);
	clearFadeInTimeOut() {
		this.timeoutFadeIn && clearTimeout(this.timeoutFadeIn);
	}

	fadeInNews = this.fadeInNews.bind(this);
	fadeInNews() {
		timing(this.opacity, {
			toValue: 1,
			duration: 500,
			easing: Easing.linear
		}).start();
	}

	convertHtmlToPdf = this.convertHtmlToPdf.bind(this);
	convertHtmlToPdf({ html, fileName = 'news', directory = 'Documents' }) {
		try {
			return new Promise((resolve, reject) => {
				let options = {
					html,
					fileName,
					directory
				};
				Business.requestAndroidPermission()
					.then(async (isFullPermission) => {
						if (isFullPermission) {
							// let file = await RNHTMLtoPDF.convert(options);
							// console.log('createPDF', file.filePath);
							return resolve();
						} else {
							Alert.alert("You don't enable storage permissions");
							resolve({});
						}
					})
					.catch(reject);
			});
		} catch (error) {
			console.log('convertHtmlToPdf error', error);
			resolve({});
		}
	}

	componentDidMount() {
		const { setRefPdf } = this.props;

		this.handleReadNews();

		setRefPdf && setRefPdf(this);
		this.clearFadeInTimeOut();
		this.timeoutFadeIn = setTimeout(this.fadeInNews, 500);
		Platform.OS === 'android' &&
			AppState.addEventListener(
				'change',
				this._handleCatchHideDialogShare
			);
	}

	saveSchedule = this.saveSchedule.bind(this);
	async saveSchedule() {
		if (this.state.html) {
			const pdfFile = await this.convertHtmlToPdf({ html: res });
			this.setState(
				() => ({
					url: pdfFile.filePath
				}),
				this.sharePdf
			);
		} else {
			this.state.url && this.sharePdf();
		}
	}

	getTitle(data) {
		if (!data) return '';
		if (!data.title) return '';
		return data.title;
	}

	getShareUrl = this.getShareUrl.bind(this);
	getShareUrl() {
		switch (this.documentType) {
			case DOCUMENT_TYPE_NEW.URL:
				return this.state.url;
			case DOCUMENT_TYPE_NEW.HTML:
			case DOCUMENT_TYPE_NEW.PDF:
				return `file://${this.state.url}`;
			case DOCUMENT_TYPE_NEW.TEXT:
				break;
			default:
				break;
		}
	}

	getShareOptions = this.getShareOptions.bind(this);
	getShareOptions() {
		switch (this.documentType) {
			case DOCUMENT_TYPE_NEW.URL:
				return {
					title: this.getTitle(this.props.data),
					message: this.getTitle(this.props.data),
					url: this.getShareUrl(),
					subject: this.getTitle(this.props.data)
				};
			case DOCUMENT_TYPE_NEW.HTML:
			case DOCUMENT_TYPE_NEW.PDF:
			case DOCUMENT_TYPE_NEW.TEXT:
				return {
					title: this.getTitle(this.props.data),
					message: this.getTitle(this.props.data),
					url: this.getShareUrl(),
					type: 'application/pdf',
					subject: this.getTitle(this.props.data),
					failOnCancel: true
				};
			default:
				break;
		}
	}

	sharePdfIos = this.sharePdfIos.bind(this);
	sharePdfIos() {
		try {
			const shareOptions = this.getShareOptions();
			Share.open(shareOptions)
				.then(() => {
					this.props.onHideShare && this.props.onHideShare();
				})
				.catch((err) => {
					this.props.onHideShare && this.props.onHideShare();
					console.info('share pdf error', err);
				});
		} catch (error) {
			this.props.onHideShare && this.props.onHideShare();
			logDevice('info', `sharePdf newsDetail exception ${error}`);
		}
	}
	_handleCatchHideDialogShare = (nextAppState) => {
		if (nextAppState === 'active') {
			if (this.isShowDialogShare) {
				this.props.onHideShare && this.props.onHideShare();
				this.isShowDialogShare = false;
			} else {
				this.isShowDialogShare = false;
			}
		} else if (
			nextAppState === 'background' ||
			nextAppState === 'inactive'
		) {
		}
	};
	sharePdfAndroid = this.sharePdfAndroid.bind(this);
	sharePdfAndroid() {
		try {
			Business.requestAndroidPermission().then((isFullPermission) => {
				if (isFullPermission && this.state.url) {
					const shareOptions = this.getShareOptions();
					Share.open(shareOptions)
						.then(() => {
							this.isShowDialogShare = true;
						})
						.catch((err) => {
							console.log('share pdf error', err);
							this.isShowDialogShare = false;
						});
				} else {
					Alert.alert("You don't enable storage permissions");
					this.isShowDialogShare = false;
				}
			});
		} catch (error) {
			logAndReport(
				'sharePdf newsDetail exception',
				error,
				'sharePdf newsDetail'
			);
			logDevice('info', `sharePdf newsDetail exception: ${error}`);
			this.isShowDialogShare = false;
		}
	}

	sharePdf = this.sharePdf.bind(this);
	sharePdf() {
		return Platform.OS === 'ios'
			? this.sharePdfIos()
			: this.sharePdfAndroid();
	}

	componentWillUnmount() {
		this.clearFadeInTimeOut();
		this.props.updateDisableShareNew &&
			this.props.updateDisableShareNew(true);
		Platform.OS === 'android' &&
			AppState.removeEventListener(
				'change',
				this._handleCatchHideDialogShare
			);
		if (this.state.url) {
			RNFetchBlob.fs
				.unlink(this.state.url)
				.then(() => {
					console.log('delete success');
				})
				.catch((error) => {
					console.log('error: ', error);
				});
		}
	}

	handleUrlNews = this.handleUrlNews.bind(this);
	handleUrlNews() {
		const { data } = this.props;
		const { directLink: link } = data;
		if (!link) {
			const error = I18n.t(NEWS_ERROR.NEWS_STORY_UNAVAILABLE);
			this.props.showThenHideError && this.props.showThenHideError(error);
		} else {
			this.props.updateDisableShareNew &&
				this.props.updateDisableShareNew(false);
		}
		return this.setState({
			url: link,
			isPdf: false,
			isLoading: false
		});
	}

	showUnavailableNew = this.showUnavailableNew.bind(this);
	async showUnavailableNew() {
		const html = TEMP; // Về sau thay bằng nội dung html bản ghi news detail
		this.setState({
			isLoading: false,
			html,
			isPdf: true
		});
		this.props.updateDisableShareNew &&
			this.props.updateDisableShareNew(false);
	}

	processGetNewError = this.processGetNewError.bind(this);
	processGetNewError(error) {
		console.log('error', error);
		const { message } = error;
		if (message && message === 'statusCode: 400') {
			return this.showUnavailableNew();
		}
		return this.getNewsError();
	}

	handleBlankNews = this.handleBlankNews.bind(this);
	handleBlankNews() {
		// Run animation show error on AffecredSymbol component
	}

	handleHtmlNews = this.handleHtmlNews.bind(this);
	handleHtmlNews() {
		// Download stream
		const { data = {} } = this.props;
		const { streamUrl: url } = data;
		Http.get({
			url,
			token: Controller.getAccessToken(),
			timeout: TIMEOUT_CANCEL_REPORT_FROM_FILE
		})
			.then(async (res) => {
				try {
					this.setState({
						isLoading: false,
						html: res,
						isPdf: true
					});
					this.props.updateDisableShareNew &&
						this.props.updateDisableShareNew(false);
				} catch (error) {
					return this.processGetNewError(error);
				}
			})
			.catch((error) => {
				return this.processGetNewError(error);
			});
	}

	getNewsError = this.getNewsError.bind(this);
	getNewsError() {
		const error = I18n.t(NEWS_ERROR.NEWS_STORY_UNAVAILABLE);
		this.props.showThenHideError && this.props.showThenHideError(error);
		return this.setState({
			html: '',
			url: '',
			isLoading: false,
			isPdf: true
		});
	}

	handlePdfNews = this.handlePdfNews.bind(this);
	handlePdfNews(suffix) {
		// Download stream
		const { data = {} } = this.props;
		let { directLink: url } = data;
		const dir = RNFetchBlob.fs.dirs;
		const now = new Date().getTime();
		this.path = `${dir.DocumentDir}/news_storage/${now}.pdf`;
		if (suffix) {
			url = url + suffix;
		}

		Http.get({
			url,
			token: Controller.getAccessToken(),
			timeout: TIMEOUT_CANCEL_REPORT_FROM_FILE
		})
			.then((res) => {
				try {
					console.log('res');
					RNFetchBlob.fs
						.writeFile(this.path, res, 'base64')
						.then(() => {
							this.setState(
								{
									isLoading: false,
									url: this.path,
									isPdf: true
								},
								() => {
									this.props.updateDisableShareNew &&
										this.props.updateDisableShareNew(false);
								}
							);
						})
						.catch((error) => {
							console.log('error', error);
							this.getNewsError();
						});
				} catch (error) {
					return this.processGetNewError(error);
				}
			})
			.catch((error) => {
				return this.processGetNewError(error);
			});
	}

	handleReadNews = this.handleReadNews.bind(this);
	handleReadNews() {
		const { directLink: storyURL, vendor_code: vendorCode } =
			this.props.data;
		if (
			vendorCode === isVendorCode &&
			storyURL.indexOf(isStoryURL) !== -1 &&
			this.documentType === DOCUMENT_TYPE_NEW.PDF
		) {
			return this.handlePdfNews('&ft=pdf'); // Truyền thêm suffix
		} else {
			switch (this.documentType) {
				case DOCUMENT_TYPE_NEW.URL:
					return this.handleUrlNews();
				case DOCUMENT_TYPE_NEW.HTML:
				case DOCUMENT_TYPE_NEW.TEXT:
					return this.handleHtmlNews();
				case DOCUMENT_TYPE_NEW.PDF:
					return this.handlePdfNews();
				default:
					break;
			}
		}
	}

	hideHeader = this.hideHeader.bind(this);
	hideHeader() {
		const { hideAffectedSymbol } = this.props;
		hideAffectedSymbol && hideAffectedSymbol();
	}

	showHeader = this.showHeader.bind(this);
	showHeader() {
		const { showAffectedSymbol } = this.props;
		showAffectedSymbol && showAffectedSymbol();
	}

	triggerHideAffectedSymbol = this.triggerHideAffectedSymbol.bind(this);
	triggerHideAffectedSymbol({ currentPage }) {
		if (currentPage === 2 && this.prePage === 1) {
			// Hide header
			if (!this.isShowAffectedSymbol) return;
			this.isShowAffectedSymbol = false;
			return this.hideHeader();
		}
		if (currentPage === 1 && this.prePage === 2) {
			// Show header
			if (this.isShowAffectedSymbol) return;
			this.isShowAffectedSymbol = true;
			return this.showHeader();
		}
	}

	renderUrlNew = this.renderUrlNew.bind(this);
	renderUrlNew() {
		const { data } = this.props;
		const { directLink: storyURL, vendor_code: vendorCode } =
			this.props.data;
		let pds = '';
		if (
			vendorCode === isVendorCode &&
			storyURL.indexOf(isStoryURL) !== -1 &&
			this.documentType === DOCUMENT_TYPE_NEW.PDF
		) {
			pds = '&ft=pdf';
		}
		let { directLink: link } = data;
		if (!link) return <View />;
		link = link.replace('http://', 'https://');
		return (
			<View
				style={{
					overflow: 'hidden',
					flex: 1,
					backgroundColor: CommonStyle.backgroundColorNews
				}}
			>
				<WebView
					originWhitelist={'*'}
					source={{ uri: link + pds }}
					onLoadEnd={() => {
						this.setState({
							isLoading: false
						});
					}}
					style={{
						flex: 1,
						backgroundColor: CommonStyle.backgroundColorNews
					}}
					decelerationRate="normal"
					scalesPageToFit={true}
				/>
			</View>
		);
	}

	renderHTMLNew = this.renderHTMLNew.bind(this);
	renderHTMLNew() {
		const html = this.state.html;
		if (!html) return <View />;
		return (
			<View
				style={{
					overflow: 'hidden',
					flex: 1,
					backgroundColor: CommonStyle.backgroundColorNews
				}}
			>
				<WebView
					originWhitelist={'*'}
					source={{ html }}
					onLoadEnd={() => {
						this.setState({
							isLoading: false
						});
					}}
					style={{
						flex: 1,
						backgroundColor: CommonStyle.backgroundColorNews
					}}
					decelerationRate="normal"
					scalesPageToFit={Platform.OS === 'ios'}
				/>
			</View>
		);
	}

	renderPDFNew = this.renderPDFNew.bind(this);
	renderPDFNew() {
		const { directLink: storyURL, vendor_code: vendorCode } =
			this.props.data;
		console.log(this.props.data + '&ft=pdf');

		if (!this.state.url) return <View />;
		const { updatePagingNews } = this.props;
		return (
			<Animated.View
				style={{
					flex: 1,
					backgroundColor: CommonStyle.backgroundColorNews,
					overflow: 'hidden'
				}}
			>
				{/* <Pdf
					style={{ flex: 1, width: '100%' }}
					scale={1}
					spacing={2}
					source={{ uri: this.state.url }}
					activityIndicator={
						<View
							style={{
								backgroundColor:
									CommonStyle.backgroundColorNews,
								flex: 1,
								width: WIDTH_DEVICE,
								alignItems: 'center'
							}}
						>
							<ProgressBar />
						</View>
					}
					onLoadComplete={(numberOfPages) => {
						this.setState({
							loaded: true
						});
						this.numberOfPages = numberOfPages;
					}}
					onPageChanged={(currentPage) => {
						updatePagingNews &&
							updatePagingNews({
								currentPage,
								numberOfPages: this.numberOfPages
							});
						// this.triggerHideAffectedSymbol({ currentPage })
						this.prePage = currentPage;
					}}
				/> */}
			</Animated.View>
		);
	}

	renderData = this.renderData.bind(this);
	renderData() {
		const { directLink: storyURL, vendor_code: vendorCode } =
			this.props.data;
		if (
			vendorCode === isVendorCode &&
			storyURL.indexOf(isStoryURL) !== -1 &&
			this.documentType === DOCUMENT_TYPE_NEW.PDF
		) {
			return this.renderPDFNew();
		}
		switch (this.documentType) {
			case DOCUMENT_TYPE_NEW.URL:
				return this.renderUrlNew();
			case DOCUMENT_TYPE_NEW.HTML:
			case DOCUMENT_TYPE_NEW.TEXT:
				return this.renderHTMLNew();
			case DOCUMENT_TYPE_NEW.PDF:
				return this.renderPDFNew();
			default:
				break;
		}
		// }
	}

	renderLoading = this.renderLoading.bind(this);
	renderLoading() {
		return (
			<View
				style={{
					flex: 1,
					justifyContent: 'center',
					alignItems: 'center'
				}}
			>
				<ProgressBar />
			</View>
		);
	}

	render() {
		return (
			<Animated.View style={{ flex: 1, opacity: this.opacity }}>
				{this.state.isLoading
					? this.renderLoading()
					: this.renderData()}
			</Animated.View>
		);
	}
}
